var offsetLeft, offsetTop, beforeInfo, launchCell, diagram, stateActions = {
  lastSave: null, 
  lastAction: null };
diagram = {};
var GRAPH_CONNECTOR = {
  name: 'rounded'
};
var GRAPH_ROUTER =  {
  name: 'manhattan'
};
